import{_ as a,b as s,o as i,a5 as t}from"./chunks/framework.CAaOUjTN.js";const c=JSON.parse('{"title":"Hexo 入门","description":"","frontmatter":{"title":"Hexo 入门","date":"2017-10-25T13:08:27.000Z","tags":["Blog","Hexo"],"license":"CC BY-NC 4.0","read":{"text":"6 min read","minutes":5.075,"time":304500,"words":1015}},"headers":[{"level":2,"title":"安装之前的准备","slug":"安装之前的准备","link":"#安装之前的准备","children":[]},{"level":2,"title":"安装 Hexo","slug":"安装-hexo","link":"#安装-hexo","children":[]},{"level":2,"title":"初始化","slug":"初始化","link":"#初始化","children":[]},{"level":2,"title":"配置","slug":"配置","link":"#配置","children":[]},{"level":2,"title":"写作","slug":"写作","link":"#写作","children":[]},{"level":2,"title":"测试","slug":"测试","link":"#测试","children":[]},{"level":2,"title":"部署到 GitHub Page","slug":"部署到-github-page","link":"#部署到-github-page","children":[{"level":3,"title":"创建 GitHub Page 仓库","slug":"创建-github-page-仓库","link":"#创建-github-page-仓库","children":[]},{"level":3,"title":"添加 hexo-deployer-git 插件","slug":"添加-hexo-deployer-git-插件","link":"#添加-hexo-deployer-git-插件","children":[]},{"level":3,"title":"配置 deploy","slug":"配置-deploy","link":"#配置-deploy","children":[]},{"level":3,"title":"部署","slug":"部署","link":"#部署","children":[]}]},{"level":2,"title":"主题","slug":"主题","link":"#主题","children":[]},{"level":2,"title":"更多","slug":"更多","link":"#更多","children":[]}],"relativePath":"posts/basic-hexo.md","filePath":"posts/basic-hexo.md"}'),l={name:"posts/basic-hexo.md"};function h(n,e,p,r,o,d){return i(),s("div",null,e[0]||(e[0]=[t(`<p>就把这篇文章算做自己的第一篇技术文章吧！以后这个博客就拿来记录自己的一些技术心得。</p><p>官方文档已经很详细了，为什么我还要写一遍呢？记录之，巩固之</p><h2 id="安装之前的准备" tabindex="-1">安装之前的准备 <a class="header-anchor" href="#安装之前的准备" aria-label="Permalink to &quot;安装之前的准备&quot;">​</a></h2><p>安装 <a href="http://nodejs.cn/" target="_blank" rel="noreferrer">Nodejs</a> 和 <a href="https://www.npmjs.com/" target="_blank" rel="noreferrer">npm</a>，推荐使用 <a href="https://github.com/creationix/nvm" target="_blank" rel="noreferrer">nvm</a> 来安装。既然都推荐使用 <a href="https://github.com/creationix/nvm" target="_blank" rel="noreferrer">nvm</a> 了，那就再推荐一下包管理工具 <a href="https://yarnpkg.com/zh-Hans/" target="_blank" rel="noreferrer">Yarn</a></p><h2 id="安装-hexo" tabindex="-1">安装 Hexo <a class="header-anchor" href="#安装-hexo" aria-label="Permalink to &quot;安装 Hexo&quot;">​</a></h2><p>如果上面安装好了，那现在就特别简单了。</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes vitesse-dark vitesse-light vp-code" tabindex="0"><code><span class="line"><span style="--shiki-dark:#80A665;--shiki-light:#59873A;">npm</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> install</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> hexo-cli</span><span style="--shiki-dark:#C99076;--shiki-light:#A65E2B;"> -g</span><span style="--shiki-dark:#758575DD;--shiki-light:#A0ADA0;"> #全局安装</span></span>
<span class="line"><span style="--shiki-dark:#80A665;--shiki-light:#59873A;">npm</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> isntall</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> hexo-cli</span><span style="--shiki-dark:#C99076;--shiki-light:#A65E2B;"> --save</span><span style="--shiki-dark:#758575DD;--shiki-light:#A0ADA0;"> #当前项目安装</span></span></code></pre></div><p>以上二选一即可，当然用 <a href="https://yarnpkg.com/zh-Hans/" target="_blank" rel="noreferrer">Yarn</a> 安装也是可以的</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes vitesse-dark vitesse-light vp-code" tabindex="0"><code><span class="line"><span style="--shiki-dark:#80A665;--shiki-light:#59873A;">yarn</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> global</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> add</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> hexo-cli</span><span style="--shiki-dark:#758575DD;--shiki-light:#A0ADA0;"> #全局安装</span></span>
<span class="line"><span style="--shiki-dark:#80A665;--shiki-light:#59873A;">yarn</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> add</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> hexo-cli</span><span style="--shiki-dark:#758575DD;--shiki-light:#A0ADA0;"> #当前项目安装</span></span></code></pre></div><h2 id="初始化" tabindex="-1">初始化 <a class="header-anchor" href="#初始化" aria-label="Permalink to &quot;初始化&quot;">​</a></h2><p>安装完成之后，那就是找个文件夹来初始化博客项目了。随便找个文件夹，运行</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes vitesse-dark vitesse-light vp-code" tabindex="0"><code><span class="line"><span style="--shiki-dark:#80A665;--shiki-light:#59873A;">hexo</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> init</span></span>
<span class="line"><span style="--shiki-dark:#80A665;--shiki-light:#59873A;">npm</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> install</span><span style="--shiki-dark:#758575DD;--shiki-light:#A0ADA0;"> # 或者 yarn</span></span></code></pre></div><p>然后，就好了～</p><blockquote><p>Tips：这里需要全局安装，若不是全局安装，则需要在安装 Hexo 的那个目录下的 config.json 里面的配置相应的命令然后使用 npm 或者 yarn 运行</p></blockquote><h2 id="配置" tabindex="-1">配置 <a class="header-anchor" href="#配置" aria-label="Permalink to &quot;配置&quot;">​</a></h2><p>配置文件为 <code>_config.yml</code> ，配置文件的详细说明 <a href="https://hexo.io/zh-cn/docs/configuration.html" target="_blank" rel="noreferrer">Hexo 配置</a></p><p>这里找到配置文件，就可以为所欲为啦！自己去魔改吧。这里还不能说是魔改，毕竟只是一些基本的配置！</p><h2 id="写作" tabindex="-1">写作 <a class="header-anchor" href="#写作" aria-label="Permalink to &quot;写作&quot;">​</a></h2><p>既然是博客，那肯定需要有写作的地方才行啊，所以要怎么写一篇新文章呢？很简单，在博客的目录运行如下命令</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes vitesse-dark vitesse-light vp-code" tabindex="0"><code><span class="line"><span style="--shiki-dark:#80A665;--shiki-light:#59873A;">hexo</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> new</span><span style="--shiki-dark:#C98A7D77;--shiki-light:#B5695977;"> &#39;</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;">新文章</span><span style="--shiki-dark:#C98A7D77;--shiki-light:#B5695977;">&#39;</span></span></code></pre></div><p>就这样，一篇新文章就这样诞生了，当然还得需要你去充实它～</p><blockquote><p>Tips：文章使用 <a href="https://github.com/Melo618/Simple-Markdown-Guide" target="_blank" rel="noreferrer">Markdown</a> 语法来排版，特别好用，不会就赶紧看看吧。十分钟即可入门，两小时即可熟练使用。</p></blockquote><h2 id="测试" tabindex="-1">测试 <a class="header-anchor" href="#测试" aria-label="Permalink to &quot;测试&quot;">​</a></h2><p>写好了文章，那要怎么看效果呢，为怎么知道我写了之后的排版到底是怎么样的呢？简单哟～，一句命令搞定</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes vitesse-dark vitesse-light vp-code" tabindex="0"><code><span class="line"><span style="--shiki-dark:#80A665;--shiki-light:#59873A;">hexo</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> s</span></span></code></pre></div><p>然后在浏览器里面打开 <a href="http://localhost:4000" target="_blank" rel="noreferrer">http://localhost:4000</a> ，就可以预览你的博客啦～</p><h2 id="部署到-github-page" tabindex="-1">部署到 GitHub Page <a class="header-anchor" href="#部署到-github-page" aria-label="Permalink to &quot;部署到 GitHub Page&quot;">​</a></h2><p><a href="https://github.com/" target="_blank" rel="noreferrer">GitHub</a> 提供了一个 <a href="https://pages.github.com/" target="_blank" rel="noreferrer">GitHub Page</a> 页面，咱们就用这个页面来存放自己的博客吧，当然也可以部署到自己的服务器上～，这里就只介绍怎么部署到 <a href="https://pages.github.com/" target="_blank" rel="noreferrer">GitHub Page</a> 上～</p><h3 id="创建-github-page-仓库" tabindex="-1">创建 GitHub Page 仓库 <a class="header-anchor" href="#创建-github-page-仓库" aria-label="Permalink to &quot;创建 GitHub Page 仓库&quot;">​</a></h3><p>创建一个仓库名为 <code>username.github.io</code> 其中 <code>username</code> 就是你的 GitHub 的用户名</p><h3 id="添加-hexo-deployer-git-插件" tabindex="-1">添加 <a href="https://github.com/hexojs/hexo-deployer-git" target="_blank" rel="noreferrer">hexo-deployer-git</a> 插件 <a class="header-anchor" href="#添加-hexo-deployer-git-插件" aria-label="Permalink to &quot;添加 [hexo-deployer-git](https://github.com/hexojs/hexo-deployer-git) 插件&quot;">​</a></h3><p>这是一个快速部署到 GitHub 的插件，安装也特别简单，一句命令搞定</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes vitesse-dark vitesse-light vp-code" tabindex="0"><code><span class="line"><span style="--shiki-dark:#80A665;--shiki-light:#59873A;">npm</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> install</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> hexo-deployer-git</span></span></code></pre></div><h3 id="配置-deploy" tabindex="-1">配置 deploy <a class="header-anchor" href="#配置-deploy" aria-label="Permalink to &quot;配置 deploy&quot;">​</a></h3><p>然后在之前介绍的配置文件中配置 deploy</p><div class="language-yml vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">yml</span><pre class="shiki shiki-themes vitesse-dark vitesse-light vp-code" tabindex="0"><code><span class="line"><span style="--shiki-dark:#B8A965;--shiki-light:#998418;">deploy</span><span style="--shiki-dark:#666666;--shiki-light:#999999;">:</span></span>
<span class="line"><span style="--shiki-dark:#B8A965;--shiki-light:#998418;">  type</span><span style="--shiki-dark:#666666;--shiki-light:#999999;">:</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> git</span></span>
<span class="line"><span style="--shiki-dark:#B8A965;--shiki-light:#998418;">  repo</span><span style="--shiki-dark:#666666;--shiki-light:#999999;">:</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> git@github.com:username/username.github.io.git</span><span style="--shiki-dark:#758575DD;--shiki-light:#A0ADA0;"> # 把 username 替换成你自己用户名</span></span></code></pre></div><h3 id="部署" tabindex="-1">部署 <a class="header-anchor" href="#部署" aria-label="Permalink to &quot;部署&quot;">​</a></h3><p>万事俱备，只欠东风～</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes vitesse-dark vitesse-light vp-code" tabindex="0"><code><span class="line"><span style="--shiki-dark:#80A665;--shiki-light:#59873A;">hexo</span><span style="--shiki-dark:#C98A7D;--shiki-light:#B56959;"> deploy</span><span style="--shiki-dark:#758575DD;--shiki-light:#A0ADA0;"> # 或者 hexo d</span></span></code></pre></div><p>之后，应该会提示你登陆 GitHub ，登陆之后就会自动部署到你的 GitHub Page ，然后你可以通过访问 username.github.io 来访问你自己的博客</p><h2 id="主题" tabindex="-1">主题 <a class="header-anchor" href="#主题" aria-label="Permalink to &quot;主题&quot;">​</a></h2><p>到了这里，你已经可是使用 Hexo 来写博客了，但是呢！默认的主题不好看，对不起，我是外貌协会的，怎么办～没关系，配置文件里面不是有 <code>theme</code> 这个配置项吗，那就说明，主题是可以更改的！</p><p><a href="https://hexo.io/themes/" target="_blank" rel="noreferrer">Hexo 主题</a> 这里有很多主题，你只需要下在下来放到 <code>themes</code>文件夹里面，然后把配置文件里面的 <code>theme</code> 改成相应的主题名字即可，当你再次测试你的博客的时候，你会发现你的主题已经改变了。</p><h2 id="更多" tabindex="-1">更多 <a class="header-anchor" href="#更多" aria-label="Permalink to &quot;更多&quot;">​</a></h2><p>到这个时候，估计你应该会有很多问题了，但是呢，你的那些问题都不是问题，你可以查阅 <a href="https://hexo.io/zh-cn/docs/" target="_blank" rel="noreferrer">Hexo 文档</a>，以及相关主题的文档来解决你的问题，当然也欢迎在下方留言，我们可以一起交流，讨论～</p>`,45)]))}const g=a(l,[["render",h]]);export{c as __pageData,g as default};
