import{_ as r,b as t,o as a,a5 as o}from"./chunks/framework.CAaOUjTN.js";const b=JSON.parse('{"title":"Ubuntu 装机清单","description":"","frontmatter":{"title":"Ubuntu 装机清单","date":"2017-09-23T01:00:49.000Z","tags":["Linux","Ubuntu"],"license":"CC BY-NC 4.0","read":{"text":"2 min read","minutes":1.72,"time":103200,"words":344}},"headers":[{"level":2,"title":"软件清单","slug":"软件清单","link":"#软件清单","children":[]},{"level":2,"title":"桌面系统","slug":"桌面系统","link":"#桌面系统","children":[{"level":3,"title":"主题","slug":"主题","link":"#主题","children":[]},{"level":3,"title":"gnome 扩展","slug":"gnome-扩展","link":"#gnome-扩展","children":[]}]},{"level":2,"title":"oh-my-zsh 主题","slug":"oh-my-zsh-主题","link":"#oh-my-zsh-主题","children":[]},{"level":2,"title":"oh-my-zsh 插件","slug":"oh-my-zsh-插件","link":"#oh-my-zsh-插件","children":[]},{"level":2,"title":"相关链接","slug":"相关链接","link":"#相关链接","children":[]}],"relativePath":"posts/ubuntu-app.md","filePath":"posts/ubuntu-app.md"}'),n={name:"posts/ubuntu-app.md"};function l(i,e,s,h,g,u){return a(),t("div",null,e[0]||(e[0]=[o('<p>第一步：更新驱动 第二步：安装 <a href="https://github.com/shadowsocks/shadowsocks-qt5" target="_blank" rel="noreferrer">shadowsocks</a>、<a href="https://github.com/JinnLynn/genpac" target="_blank" rel="noreferrer">genpac</a> 第三步：安装 <a href="https://www.google.com/chrome/browser/desktop/index.html" target="_blank" rel="noreferrer">chrome</a></p><p>然后就可以愉快的安装其他软件了。</p><h2 id="软件清单" tabindex="-1">软件清单 <a class="header-anchor" href="#软件清单" aria-label="Permalink to &quot;软件清单&quot;">​</a></h2><ul><li><a href="https://github.com/liuqun/njit8021xclient" target="_blank" rel="noreferrer">njit8021xclient</a>：校园网</li><li><a href="http://pinyin.sogou.com/linux/?r=pinyin" target="_blank" rel="noreferrer">搜狗输入法</a>：Linux 版的中文输入法</li><li><a href="http://ohmyz.sh/" target="_blank" rel="noreferrer">oh-my-zsh</a>：替换 bash ，漂亮的命令行</li><li><a href="https://chromium.woolyss.com/#linux" target="_blank" rel="noreferrer">chromium</a>：Chrome的工程版或称实验版</li><li><a href="https://cerebroapp.com/" target="_blank" rel="noreferrer">cerebro</a>：一个好用的快速启动工具，插件丰富</li></ul><h2 id="桌面系统" tabindex="-1">桌面系统 <a class="header-anchor" href="#桌面系统" aria-label="Permalink to &quot;桌面系统&quot;">​</a></h2><p>现在 Ubuntu 可以使用 <a href="https://ubuntugnome.org/" target="_blank" rel="noreferrer">Gnome</a> 桌面了。终于可以愉快的玩耍啦，<a href="https://www.gnome-look.org/" target="_blank" rel="noreferrer">gnome 主题列表</a>、<a href="https://extensions.gnome.org/" target="_blank" rel="noreferrer">gnome 扩展列表</a></p><ul><li><a href="https://github.com/nzjrs/gnome-tweak-tool" target="_blank" rel="noreferrer">gnome-tweak-tool</a>：gnome 主题管理工具</li></ul><h3 id="主题" tabindex="-1">主题 <a class="header-anchor" href="#主题" aria-label="Permalink to &quot;主题&quot;">​</a></h3><ul><li><a href="https://www.gnome-look.org/p/1013698/" target="_blank" rel="noreferrer">Vimix Gtk Theme</a>：一款 gnome 主题</li></ul><h3 id="gnome-扩展" tabindex="-1">gnome 扩展 <a class="header-anchor" href="#gnome-扩展" aria-label="Permalink to &quot;gnome 扩展&quot;">​</a></h3><ul><li><a href="https://extensions.gnome.org/extension/570/todotxt/" target="_blank" rel="noreferrer">todo</a>：任务清单</li><li><a href="https://extensions.gnome.org/extension/19/user-themes/" target="_blank" rel="noreferrer">User Themes</a>：主题支持扩展</li><li><a href="https://extensions.gnome.org/extension/495/topicons/" target="_blank" rel="noreferrer">TopIcons</a>：把图标放在顶部栏</li><li><a href="https://extensions.gnome.org/extension/307/dash-to-dock/" target="_blank" rel="noreferrer">Dash to Dock</a>：把 dash 变成 dock</li><li><a href="https://extensions.gnome.org/extension/1036/extensions/" target="_blank" rel="noreferrer">Extensions</a>：快速管理 gnome 扩展</li><li><a href="https://extensions.gnome.org/extension/779/clipboard-indicator/" target="_blank" rel="noreferrer">Clipboard Indicator</a>：粘贴板历史记录</li><li><a href="https://extensions.gnome.org/extension/358/activities-configurator/" target="_blank" rel="noreferrer">Activities Configurator</a>：自定义顶部栏</li><li><a href="https://extensions.gnome.org/extension/15/alternatetab/" target="_blank" rel="noreferrer">AlternateTab</a>：像 windows 一样切换应用</li><li><a href="https://extensions.gnome.org/extension/72/recent-items/" target="_blank" rel="noreferrer">Recent Items</a>：最近浏览的文件的历史记录</li><li><a href="https://extensions.gnome.org/extension/7/removable-drive-menu/" target="_blank" rel="noreferrer">Removable Drive Menu</a>：USB 等外接硬盘移除菜单</li></ul><h2 id="oh-my-zsh-主题" tabindex="-1">oh-my-zsh 主题 <a class="header-anchor" href="#oh-my-zsh-主题" aria-label="Permalink to &quot;oh-my-zsh 主题&quot;">​</a></h2><p><a href="https://github.com/robbyrussell/oh-my-zsh/wiki/Themes" target="_blank" rel="noreferrer">主题列表</a></p><ul><li><a href="https://github.com/bhilburn/powerlevel9k" target="_blank" rel="noreferrer">Powerlevel9k</a>：我使用的主题</li></ul><h2 id="oh-my-zsh-插件" tabindex="-1">oh-my-zsh 插件 <a class="header-anchor" href="#oh-my-zsh-插件" aria-label="Permalink to &quot;oh-my-zsh 插件&quot;">​</a></h2><p><a href="https://github.com/robbyrussell/oh-my-zsh/wiki/Plugins" target="_blank" rel="noreferrer">插件列表</a></p><ul><li><a href="https://github.com/robbyrussell/oh-my-zsh/wiki/Plugin:git" target="_blank" rel="noreferrer">git</a>：git 命令别名工具</li><li><a href="https://github.com/wting/autojump" target="_blank" rel="noreferrer">autojump</a>：在路径之间旋转、跳跃</li><li><a href="https://github.com/zsh-users/zsh-autosuggestions" target="_blank" rel="noreferrer">zsh-autosuggestions</a>：命令的历史记录</li><li><a href="https://github.com/robbyrussell/oh-my-zsh/tree/master/plugins/extract" target="_blank" rel="noreferrer">extract</a>：超级好用的解压命令，一个 <code>x</code> 可以解压大部分压缩包</li></ul><h2 id="相关链接" tabindex="-1">相关链接 <a class="header-anchor" href="#相关链接" aria-label="Permalink to &quot;相关链接&quot;">​</a></h2><ul><li><a href="https://alim0x.gitbooks.io/awesome-linux-software-zh_cn/content/" target="_blank" rel="noreferrer">超赞的 Linux 软件</a></li></ul>',19)]))}const c=r(n,[["render",l]]);export{b as __pageData,c as default};
